Go<pre><code>// tmux/tmux_test.go
package tmux

import (
    "testing"
    "github.com/stretchr/testify/assert"
)

func TestListSessions(t *testing.T) {
    tests := []struct {
        name        string
        shellOutput string
        expected    []*Session
        expectError bool
    }{
        {
            name:        "single session",
            shellOutput: "session1::/path/to/project",
            expected: []*Session{
                {Name: "session1", Path: "/path/to/project"},
            },
            expectError: false,
        },
        {
            name:        "empty output",
            shellOutput: "",
            expected:    []*Session{},
            expectError: false,
        },
    }

    for _, tt := range tests {
        t.Run(tt.name, func(t *testing.T) {
            mockShell := new(shell.MockShell)
            mockShell.On("Cmd", "tmux", "list-sessions", "-F", "...").
                Return(tt.shellOutput, nil)

            tmux := NewTmux(mockShell)
            result, err := tmux.ListSessions()

            if tt.expectError {
                assert.Error(t, err)
            } else {
                assert.NoError(t, err)
                assert.Equal(t, tt.expected, result)
            }

            mockShell.AssertExpectations(t)
        })
    }
}
