Go<pre><code>// ui/menu.go
package ui

import tea "github.com/charmbracelet/bubbletea"

type Model struct {
    items    []Item
    selected int
    filter   string
}

func NewModel(items []Item) Model {
    return Model{
        items:    items,
        selected: 0,
    }
}

func (m Model) Init() tea.Cmd {
    return nil
}

func (m Model) Update(msg tea.Msg) (tea.Model, tea.Cmd) {
    switch msg := msg.(type) {
    case tea.KeyMsg:
        switch msg.String() {
        case "q", "ctrl+c":
            return m, tea.Quit
        case "j", "down":
            if m.selected &lt; len(m.items)-1 {
                m.selected++
            }
        case "k", "up":
            if m.selected &gt; 0 {
                m.selected--
            }
        case "enter":
            // Execute selected item
            return m, tea.Quit
        }
    }
    return m, nil
}

func (m Model) View() string {
    var s string
    for i, item := range m.items {
        cursor := " "
        if i == m.selected {
            cursor = "&gt;"
        }
        s += fmt.Sprintf("%s %s\n", cursor, item.Name)
    }
    return s
}
</code></pre>

