Go<pre><code>// tui/model.go
package tui

import (
    tea "github.com/charmbracelet/bubbletea"
    "github.com/your/project/session"
)

type Model struct {
    sessions []string
    cursor   int
    manager  *session.SessionManager
}

func InitialModel(manager *session.SessionManager) Model {
    return Model{
        sessions: []string{},
        cursor:   0,
        manager:  manager,
    }
}

type sessionsLoadedMsg struct {
    sessions []string
}

func (m Model) Init() tea.Cmd {
    return loadSessions(m.manager)
}

func loadSessions(manager *session.SessionManager) tea.Cmd {
    return func() tea.Msg {
        sessions, err := manager.ListSessions()
        if err != nil {
            return errMsg{err}
        }
        return sessionsLoadedMsg{sessions}
    }
}

type errMsg struct{ err error }

func (m Model) Update(msg tea.Msg) (tea.Model, tea.Cmd) {
    switch msg := msg.(type) {
    case tea.KeyMsg:
        switch msg.String() {
        case "up", "k":
            if m.cursor &gt; 0 {
                m.cursor--
            }
        case "down", "j":
            if m.cursor &lt; len(m.sessions)-1 {
                m.cursor++
            }
        case "enter":
            if len(m.sessions) &gt; 0 {
                return m, attachSession(m.manager, m.sessions[m.cursor])
            }
        case "q":
            return m, tea.Quit
        }

    case sessionsLoadedMsg:
        m.sessions = msg.sessions

    case errMsg:
        // Handle error
        return m, tea.Quit
    }

    return m, nil
}

func attachSession(manager *session.SessionManager, name string) tea.Cmd {
    return func() tea.Msg {
        err := manager.AttachSession(name)
        if err != nil {
            return errMsg{err}
        }
        return tea.Quit()
    }
}

func (m Model) View() string {
    s := "Select a tmux session:\n\n"

    for i, session := range m.sessions {
        cursor := " "
        if m.cursor == i {
            cursor = "&gt;"
        }
        s += fmt.Sprintf("%s %s\n", cursor, session)
    }

    s += "\nPress q to quit.\n"
    return s
}
